                   var setMaxJourneyDate = function(){
                       var destFrom = $("#dest_from").val();
                       var destTo = $("#dest_to").val();
               
                       var internationalCityIdArray = JSON.parse('[81,872]');
                       var selectedFromStationObject = cities.filter(function(station){return station.city_name.toLowerCase() === destFrom.toLowerCase()});
                       var selectedToStationObject = cities.filter(function(station){return station.city_name.toLowerCase() === destTo.toLowerCase()});
               
                       var isInternational = internationalCityIdArray.filter(function(cityId){
                           let found = false;
                           if(selectedFromStationObject.length > 0 && cityId === selectedFromStationObject[0].city_id){
                               found = true;
                           }
                           if(selectedToStationObject.length > 0 && cityId === selectedToStationObject[0].city_id){
                               found = true;
                           }
                           return found;
                       });
               
                       if(isInternational.length > 0){
                           maxJourneyDate = internationalMaxJourneyDate;
                       }else{
                           // Keep or remove previous value from doj based on train nationality.
                           if($("#doj").val()){
                               var todayDate = new Date();
                               var selectedDate = new Date($("#doj").val());
                               var diffTime = Math.abs(selectedDate - todayDate);
                               var diffDays = Math.ceil(diffTime / 86400000);
                               if(diffDays > usualMaxJourneyDate){
                                   $("#doj").val('');
                               }
                           }
                           maxJourneyDate = usualMaxJourneyDate;
                       }
                       $('.datepicker').datepicker('option', 'maxDate', maxJourneyDate);
                   }
               
