               function validateTrainForm(formObj) {
                   if(window.navigator.onLine === false){
                       $('.search-widget-button-and-error .railway-form-error').remove();
                       $('.search-widget-button-and-error').prepend('<div class="railway-form-error">Please check your internet connection.</div>');
                       return false;
                   }
               
                   $('.route-search-error').hide();
                   $('.search-widget-button-and-error .railway-form-error').remove();
                   var $fromCityObj = $(formObj).find('input[name="dest_from"]');
                   var $toCityObj = $(formObj).find('input[name="dest_to"]');
                   var $jDateObj = $(formObj).find('input[name="doj"]');
                   var $chooseClass = $(formObj).find('select[name="choose_class"]');
                   if (
                       $fromCityObj.val() == '' ||
                       $toCityObj.val() == '' ||
                       $jDateObj.val() == '' ||
                       $chooseClass.val() == '' ||
                       !validateCity($fromCityObj.val()) ||
                       !validateCity($toCityObj.val())
                   ) {
                       if ($chooseClass.val() == '') {
                           $('.search-widget-button-and-error').prepend('<div class="railway-form-error">Please select a Class.</div>');
                       }
                       if ($jDateObj.val() == '') {
                           $('.search-widget-button-and-error').prepend('<div class="railway-form-error">Please select date of your journey.</div>');
                       }
                       if ($toCityObj.val() == '') {
                           $('.search-widget-button-and-error').prepend('<div class="railway-form-error">Please choose destination station.</div>');
                       }
                       if($fromCityObj.val() == ''){
                           $('.search-widget-button-and-error').prepend('<div class="railway-form-error">Please choose departure station.</div>');
                       }
               
                       if($fromCityObj.val() == ''){
                           $fromCityObj.focus();
                       }else if ($toCityObj.val() == '') {
                           $toCityObj.focus();
                       }else if ($jDateObj.val() == '') {
                           $jDateObj.focus();
                       }else if ($chooseClass.val() == '') {
                           $chooseClass.focus();
                       }
               
                       if(
                           ($fromCityObj.val() != '' || $toCityObj.val() != '') &&
                           (!validateCity($fromCityObj.val()) || !validateCity($toCityObj.val()))
                       ){
                           $('.search-widget-button-and-error').prepend('<div class="railway-form-error">Please enter correct station name (or choose from suggested stations)</div>');
                       }
                   } else {
                       $(".loading").show();
                       $.ajax({
                           type: 'GET',
                           url: 'https://eticket.railway.gov.bd/booking/train/search-route',
                           data: {
                               'fromcity': $fromCityObj.val(),
                               'tocity': $toCityObj.val(),
                               'doj': $jDateObj.val(),
                               'class': $chooseClass.val()
                           },
                           success: function (data) {
                               var searchUrl = 'https://eticket.railway.gov.bd/booking/train/search/en?fromcity=' + $fromCityObj.val() + '&tocity=' + $toCityObj.val() + '&doj=' + $jDateObj.val() + '&class=' + $chooseClass.val();
                               if (typeof operatorFilterId != 'undefined') {
                                   searchUrl += '&operator=' + operatorFilterId;
                               }
                               //Track Facebook Event - Search
                               var searchStringForFb = $fromCityObj.val() + ' to ' + $toCityObj.val();
                               if(typeof fbq !== 'undefined'){
                                   fbq('track', 'Search', {
                                       search_string: searchStringForFb,
                                       journey_date: $jDateObj.val()
                                   });
                               }
               
                               // Start of CleverTap Integration
                               // Event: [B] Searched Buses
                               /*
                               var now = new Date();
                               var cleverTapData = {
                                   'Origin': $fromCityObj.val(),
                                   'Destination': $toCityObj.val(),
                                   'Journey Date': new Date($jDateObj.val() + ' ' + now.getHours() + ':' + now.getMinutes() + ':' + now.getSeconds()),
                                   'Event Source': 'Web',
                                   'Search Type': 'Journey'
                               };
                               clevertap.event.push('[B] Searched Buses', cleverTapData);
                               */
                               // End of CleverTap Integration
               
                               //Google Tag Manager - Tracking Start
                               var journeyDate = $.datepicker.formatDate('yy-mm-dd', new Date($('#doj').val()));
                               var returnDate = $.datepicker.formatDate('yy-mm-dd', new Date($('#doj').val()));
               
                               if(typeof gtag !== 'undefined'){
                                   gtag('event', 'page_view', {
                                       'send_to': 'AW-812588262',
                                       'travel_destid': $toCityObj.val(),
                                       'travel_originid': $fromCityObj.val(),
                                       'travel_pagetype': 'SearchPage',
                                       'travel_startdate': journeyDate,
                                       'travel_enddate': returnDate
                                   });
                               }
                               //Google Tag Manager - Tracking End
                               //$(".loading").hide();
                               if (data.type == 'error') {
                                   $(".loading").hide(100);
                                   $('.route-search-error').show();
                                   $('.route-search-error-msg').text(data.message);
                                   return false;
                               } else {
                                   $('.route-search-error').hide();
                                   window.location.href = searchUrl;
                               }
                           }
                       });
                   }
                   return false;
               }
            </script>
