                 'travel_pagetype': '/searchpage',
                 'travel_startdate': travel_startdate,
                 'travel_totalvalue': 'null'
             });
         }
         
         // Counter related script
         function getTicketStatistics(type) {
             var currentSelectedDate = new Date($(".date-text-wrapper").attr('data-date').replace(" ", ""));
             var dateForRequest = new Date(currentSelectedDate);
             if(type === 'NEXT'){
                 var dateLimit = new Date(new Date().toLocaleDateString("en-US"));
                 dateLimit.setDate(dateLimit.getDate() + parseInt('10'));
                 if(currentSelectedDate.getTime() < dateLimit.getTime()){
                     dateForRequest.setDate(dateForRequest.getDate() + 1);
                 }
             }
             else if(type === 'PREV'){
                 var todayDate = new Date();
                 if(currentSelectedDate.getTime() > todayDate.getTime()){
                     dateForRequest.setDate(dateForRequest.getDate() - 1);
                 }
             }
             let requestedDate = getFormattedDate(dateForRequest,'-', 'UK');
             console.log(requestedDate);
             $(".date-text-wrapper")
                 .text(getFormattedDate(dateForRequest, '-', 'UK'))
                 .attr('data-date', getFormattedDate(dateForRequest, "/"));
             $(`#${requestedDate}-available-tickets`).show().siblings().hide();
         }
         
         function getAnalytics()
         {
             $.ajax({
                 type: 'post',
                 url: '/statistics/tickets-available',
                 dataType: 'json',
                 success: function(res) {
                     const dates = Object.keys(res.ticket_available);
                     dates.forEach((date) => {
                         var onlineCount = res.ticket_available[date].online !== null ? res.ticket_available[date].online : 0;
                         var offlineCount = res.ticket_available[date].offline !== null ? res.ticket_available[date].offline : 0;
                         if($(`#${date}-merged`).length)
                         {
                             $(`#${date}-merged`).html(parseInt(onlineCount) + parseInt(offlineCount))
                         }
                         else
                         {
                             $(`#${date}-online`).html(onlineCount)
                             $(`#${date}-offline`).html(offlineCount)
                         }
         
                         $('#last-updated-datetime').html(res.updated_at)
                         $('#active-users-count').html(res.active_users_count)
                         $('#logged-in-users-count').html(res.logged_in_users_count)
                     })
                 },
                 error: function (e) {
                     if($(`#${date}-merged`).length) {
                         $(`#${date}-merged`).html('...');
                     } else {
                         $(`#${date}-online`).html('...');
                         $(`#${date}-offline`).html('...');
                     }
                 },
                 complete: function() {
                     $('.counter-loader').hide();
                 }
             });
         }
         
         
                     getAnalytics();
                         setInterval(function(){
                 getAnalytics();
             }, 600000);
                     
         
         function getFormattedDate(date, separator = '/', format = 'US') {
             let year = date.getFullYear();
             let month = (1 + date.getMonth()).toString().padStart(2, '0');
             let day = date.getDate().toString().padStart(2, '0');
             if(format === 'US'){
                 return month + separator + day + separator + year;
             }else if(format === 'UK'){
                 return day + separator + month + separator + year;
             }
         }
         
      </script>
